CREATE TABLE IF NOT EXISTS `logging_event` (`timestmp` bigint(20) NOT NULL,`formatted_message` text NOT NULL,`logger_name` varchar(254) NOT NULL,`level_string` varchar(254) NOT NULL,`thread_name` varchar(254) DEFAULT NULL,`reference_flag` smallint(6) DEFAULT NULL,`arg0` varchar(254) DEFAULT NULL,`arg1` varchar(254) DEFAULT NULL,`arg2` varchar(254) DEFAULT NULL,`arg3` varchar(254) DEFAULT NULL,`caller_filename` varchar(254) NOT NULL,`caller_class` varchar(254) NOT NULL,`caller_method` varchar(254) NOT NULL,`caller_line` char(4) NOT NULL,`marker_type` varchar(254) DEFAULT NULL,`event_id` bigint(20) NOT NULL AUTO_INCREMENT, PRIMARY KEY (`event_id`));
CREATE TABLE IF NOT EXISTS `logging_event_exception` (`event_id` bigint(20) NOT NULL,`i` smallint(6) NOT NULL,`trace_line` varchar(254) NOT NULL, PRIMARY KEY (`event_id`,`i`));
CREATE TABLE IF NOT EXISTS `logging_event_property` (`event_id` bigint(20) NOT NULL,`mapped_key` varchar(254) NOT NULL,`mapped_value` text,PRIMARY KEY (`event_id`,`mapped_key`));
ALTER TABLE `logging_event_exception` ADD CONSTRAINT `logging_event_exception_ibfk_1` FOREIGN KEY (`event_id`) REFERENCES `logging_event` (`event_id`);
ALTER TABLE `logging_event_property` ADD CONSTRAINT `logging_event_property_ibfk_1` FOREIGN KEY (`event_id`) REFERENCES `logging_event` (`event_id`);